&lt;property&gt;
 &lt;name&gt;yarn.log.server.url&lt;&#x2F;name&gt;
 &lt;value&gt;http:&#x2F;&#x2F;master:19888&#x2F;jobhistory&#x2F;logs&#x2F;&lt;&#x2F;value&gt;
 &lt;description&gt;URL for job history server&lt;&#x2F;description&gt;
&lt;&#x2F;property&gt;
&lt;property&gt;
   &lt;name&gt;yarn.nodemanager.vmem-check-enabled&lt;&#x2F;name&gt;
    &lt;value&gt;false&lt;&#x2F;value&gt;
